package com.openclassrooms.mddapi.payload.response;import lombok.Getter;import lombok.Setter;import java.time.LocalDateTime;@Getter@Setterpublic class UserResponse {    private Long id;    private String email;    private String name;    private String role;    private String firstName;    private String lastName;    private String profile_image;    private LocalDateTime createdAt;    private LocalDateTime updatedAt;    public UserResponse(Long id,String name, String firstName, String lastName ,String email,String role,String profile_image, LocalDateTime createdAt, LocalDateTime updatedAt) {        this.id = id;        this.name = name;        this.email = email;        this.role = role;        this.firstName=firstName;        this.lastName=lastName;        this.profile_image=profile_image;        this.createdAt = createdAt;        this.updatedAt = updatedAt;    }}